                    rotorFrame: {
                        r: 40,
                        fill: "#eee",
                        stroke: "#666",
                        strokeWidth: 2
                    },
                    rotorBackground: {
                        r: 34,
                        fill: "#777",
                        stroke: "#222",
                        strokeWidth: 1,
                        style: {
