                        stroke: "#222",
                        strokeWidth: 3,
                        fill: "#bbb"
                    }
                },
                ports: {
                    groups: {
                        pipes: {
                            position: {
                                name: "line",
                                args: {
                                    start: { x: "calc(w / 2)", y: "calc(h)" },
                                    end: { x: "calc(w / 2)", y: 0 }
                                }
                            },
                            markup: util.svg`
                        <rect @selector="pipeBody" />
                        <rect @selector="pipeEnd" />
                    `,
                            size: { width: 80, height: 30 },
                            attrs: {
                                portRoot: {
                                    magnetSelector: "pipeEnd"
                                },
                                pipeBody: {
                                    width: "calc(w)",
                                    height: "calc(h)",
                                    y: "calc(h / -2)",
                                    fill: {
                                        type: "linearGradient",
                                        stops: [
                                            { offset: "0%", color: "gray" },
                                            { offset: "30%", color: "white" },
                                            { offset: "70%", color: "white" },
                                            { offset: "100%", color: "gray" }
                                        ],
                                        attrs: {
                                            x1: "0%",
                                            y1: "0%",
                                            x2: "0%",
                                            y2: "100%"
                                        }
                                    }
                                },
                                pipeEnd: {
                                    width: 10,
                                    height: "calc(h+6)",
                                    y: "calc(h / -2 - 3)",
