                let flags = dia.ElementView.prototype.confirmUpdate.call(this, ...args);
                this.animateLiquid();
                if (this.hasFlag(flags, OPEN_FLAG)) {
                    this.updateCover();
                    flags = this.removeFlag(flags, OPEN_FLAG);
                }
                return flags;
