descriptor.controllerScript:
self.onInit = function() {
	const {
		dia,
		shapes,
		util,
		ui
	} = joint;
	console.log("-", joint);
	const paperContainerEl = document.getElementById("paper-container");
	const toolbarContainerEl = document.getElementById("toolbar-container");

	// Custom view flags
	const POWER_FLAG = "POWER";
	const LIGHT_FLAG = "LIGHT";
	const FLOW_FLAG = "FLOW";
	const OPEN_FLAG = "OPEN";

	// Constants
	const LIQUID_COLOR = "#0EAD69";
	const MAX_LIQUID_COLOR = "#ED2637";
	const MIN_LIQUID_COLOR = "#FFD23F";
	const START_LIQUID = 70;
	const PRESSURE_COLOR = "#1446A0";
	const MAX_PRESSURE_COLOR = "#ED2637";

	document.documentElement.style.setProperty("--liquid-color", LIQUID_COLOR);

	// Pump metrics
	const r = 30;
	const d = 10;
	const l = (3 * r) / 4;
	const step = 20;
	console.log("dia", joint);

	class Motor extends dia.Element {
		defaults() {
			return {
				...super.defaults,
				type: "Motor",
				size: {
					width: 50,
					height: 80
				},
				attrs: {
					body: {
						width: 'calc(w)',
						height: 'calc(h)',
						stroke: 'black',
						rx: 10,
						ry: 20,
						fill: {
							type: "linearGradient",
							stops: [{
									offset: "0%",
									color: "gray"
								},
								{
									offset: "30%",
									color: "white"
								},
								{
									offset: "70%",
									color: "white"
								},
								{
									offset: "100%",
									color: "gray"
								}
							]
						}
					},
					label: {
						text: "Motor Name",
						textAnchor: "middle",
						textVerticalAnchor: "bottom",
						x: "calc(w / 2)",
						y: -10,
						fontSize: 14,
						fontFamily: "sans-serif",
						fill: "#350100"
					},
					strip: { // tupi
						d: "M 0,0 H calc(w) V calc(h/8) H 0 Z",
						transform: "translate(0, calc(h / 5))",
						stroke: "#333",
						strokeWidth: 1,
						rx: 5,
						ry: 5,
						fill: "none"
					},
					rotor: {
						d: "M 0 0 H calc(w /6) V calc(h/4) H 0 Z",
						transform: "translate(calc(w / 2.5) , calc(h))",
						stroke: "#333",
						strokeWidth: 2,
						rx: 5,
						ry: 5,
						fill: "none"
					},
					ckt: {
						width: 'calc(w/2)',
						height: 'calc(h/2)',
						transform: "translate(calc(w / 4) , calc(h/2.6))",
						stroke: "#333",
						strokeWidth: 2,
						rx: 5,
						ry: 5,
						fill: "none"
					},
					r_status: {
						width: 'calc(w/4)',
						height: 'calc(h/6)',
						transform: "translate(calc(w / 2.7) , calc(h/2.6))",
						stroke: "#333",
						strokeWidth: 1,
						rx: 5,
						ry: 5,
						fill: "red"
					}
				}
			};
		}
		preinitialize() {
			this.markup = util.svg /* xml */ `
            <text @selector="label" />
            <rect @selector="body" />
            <path @selector="strip" />
            <rect @selector="ckt" />
            <rect @selector="r_status" />
            <path @selector="rotor" />
        `;
		}

	}

	class Pump extends dia.Element {
		defaults() {
			return {
				...super.defaults,
				type: "Pump",
				size: {
					width: 100,
					height: 100
				},
				power: 0,
				attrs: {
					root: {
						magnetSelector: "body"
					},
					body: {
						rx: "calc(w / 2)",
						ry: "calc(h / 2)",
						cx: "calc(w / 2)",
						cy: "calc(h / 2)",
						stroke: "gray",
						strokeWidth: 2,
